mapper.select:
SELECT r.deliveryResumeId, r. NAME, r.jobSeekerName, r.sex, r.city, r.workDay, r.internshipTime, r.arrivalDay, r.graduationSchool, r.education, r.major, r.graduationTime, i.interview_time interviewTime, r.status status, r.companyId, r.companyName, r.companyLogo FROM ( SELECT dr.id deliveryResumeId, r.name name, js.name jobSeekerName, js.sex sex, js.city city, r.work_day workDay, r.internship_time internshipTime, r.arrival_day arrivalDay, js.graduation_school graduationSchool, js.education education, js.major major, js.graduation_time graduationTime, dr.status, p.name companyName, p.id companyId, p.logo companyLogo FROM job_seeker js, delivery_resume dr, resume r, position p WHERE r.job_seeker_id = js.id AND dr.position_id = p.id AND dr.resume_id = r.id
) r LEFT JOIN interview i ON r.deliveryResumeId = i.delivery_resume_id
order by last_access desc LIMIT #{starNum},#{size}
